// Query type detection for demonstration
const items_data = items[0].json;
const query = items_data.active_query;

// Simple query classification logic
function classifyQuery(query) {
  const queryLower = query.toLowerCase();

  // Factual queries - benefit from sparse (exact terms)
  if (queryLower.includes('what is') || queryLower.includes('define') || queryLower.includes('meaning')) {
    return {
      type: 'factual',
      description: 'Definition/factual query â†’ sparse-heavy weights',
      expected_routing: 'Sparse-heavy (exact term matching)',
      predicted_alpha: 0.3
    };
  }

  // Conceptual queries - benefit from dense (semantic)
  if (queryLower.includes('how does') || queryLower.includes('explain') || queryLower.includes('why')) {
    return {
      type: 'conceptual',
      description: 'Conceptual query â†’ dense-heavy weights', 
      expected_routing: 'Dense-heavy (semantic understanding)',
      predicted_alpha: 0.7
    };
  }

  // Procedural queries - need exact steps (sparse)
  if (queryLower.includes('how to') || queryLower.includes('steps') || queryLower.includes('procedure')) {
    return {
      type: 'procedural',
      description: 'Procedural query â†’ sparse-heavy weights',
      expected_routing: 'Sparse-heavy (step-by-step matching)',
      predicted_alpha: 0.4
    };
  }

  // Default - exploratory queries
  return {
    type: 'exploratory',
    description: 'Exploratory query â†’ balanced weights',
    expected_routing: 'Balanced approach',
    predicted_alpha: 0.5
  };
}

const classification = classifyQuery(query);

const output = {
  ...items_data,
  query_analysis: {
    query: query,
    detected_type: classification.type,
    description: classification.description,
    expected_routing: classification.expected_routing,
    predicted_alpha: classification.predicted_alpha,
    reasoning: `The system detected "${query}" as a ${classification.type} query and will route accordingly.`
  }
};

return [{ json: output }];

// Combine query analysis with API response
const apiResponse = items[0].json;
const queryAnalysis = items[0].json.query_analysis;

// Create comprehensive adaptive results
const output = {
  query: apiResponse.query,
  method: 'adaptive',

  // Pre-analysis (what we predicted)
  predicted_analysis: {
    query_type: queryAnalysis.detected_type,
    predicted_alpha: queryAnalysis.predicted_alpha,
    reasoning: queryAnalysis.reasoning
  },

  // Actual results (what the API returned)
  actual_results: {
    method_used: apiResponse.method_used,
    alpha_used: apiResponse.alpha,
    answer: apiResponse.answer,
    timing_ms: apiResponse.timing?.total_ms || 0,
    context_count: apiResponse.contexts?.length || 0
  },

  // Detailed contexts with scores
  contexts_detailed: (apiResponse.contexts || []).slice(0, 3).map(ctx => ({
    title: ctx.title,
    dense_score: ctx.sd || ctx.sim_dense || 0,
    sparse_score: ctx.ss || ctx.sim_sparse || 0,
    hybrid_score: ctx.hybrid || 0,
    snippet: (ctx.content || '').substring(0, 150) + '...'
  })),

  // Analysis comparison
  analysis_accuracy: {
    prediction_correct: Math.abs((queryAnalysis.predicted_alpha || 0.5) - (apiResponse.alpha || 0.5)) < 0.2,
    alpha_difference: Math.abs((queryAnalysis.predicted_alpha || 0.5) - (apiResponse.alpha || 0.5)),
    note: 'Comparing our prediction vs actual system routing'
  },

  success: apiResponse.success,
  raw_response: apiResponse
};

// Add visual status
if (apiResponse.success) {
  output.status = 'ðŸ§  ADAPTIVE SUCCESS';
  if (output.analysis_accuracy.prediction_correct) {
    output.prediction_status = 'âœ… Prediction matched system routing!';
  } else {
    output.prediction_status = 'ðŸ”„ System chose different routing (this is normal)';
  }
} else {
  output.status = 'âŒ ERROR';
}

return [{ json: output }];
