// Create separate requests for each RAG method
const testQuery = items[0].json.test_query;
const topK = items[0].json.top_k;

// Define all three methods to test
const methods = [
  {
    name: 'naive',
    description: 'Single dense embedding search',
    params: {
      query: testQuery,
      method: 'naive',
      top_k: topK,
      include_metadata: true,
      include_timing: true
    }
  },
  {
    name: 'hybrid',
    description: 'Dense + sparse with balanced weights',
    params: {
      query: testQuery,
      method: 'hybrid',
      top_k: topK,
      alpha: 0.5,
      include_metadata: true,
      include_timing: true
    }
  },
  {
    name: 'adaptive',
    description: 'Intelligent routing based on query type',
    params: {
      query: testQuery,
      method: 'adaptive',
      top_k: topK,
      include_metadata: true,
      include_timing: true
    }
  }
];

// Return array of method configurations
return methods.map(method => ({ json: method }));

// Process individual method results
const methodConfig = items[0].json;
const apiResponse = items[1].json;

// Extract key metrics
const result = {
  method_name: methodConfig.name,
  method_description: methodConfig.description,

  // Performance metrics
  performance: {
    response_time_ms: apiResponse.timing?.total_ms || 0,
    context_count: apiResponse.contexts?.length || 0,
    success: apiResponse.success
  },

  // Quality indicators
  quality: {
    answer_length: (apiResponse.answer || '').length,
    top_similarity: apiResponse.contexts?.[0]?.hybrid || apiResponse.contexts?.[0]?.sim_dense || 0,
    context_diversity: new Set((apiResponse.contexts || []).map(c => c.title)).size
  },

  // Full response for detailed analysis
  full_response: apiResponse,

  // Method-specific details
  method_details: {
    alpha: apiResponse.alpha,
    method_used: apiResponse.method_used,
    query: apiResponse.query
  },

  // Top context preview
  top_context: apiResponse.contexts?.[0] ? {
    title: apiResponse.contexts[0].title,
    snippet: (apiResponse.contexts[0].content || '').substring(0, 200) + '...',
    scores: {
      dense: apiResponse.contexts[0].sd || apiResponse.contexts[0].sim_dense || 0,
      sparse: apiResponse.contexts[0].ss || apiResponse.contexts[0].sim_sparse || 0,
      hybrid: apiResponse.contexts[0].hybrid || 0
    }
  } : null
};

return [{ json: result }];

// Create comprehensive comparison dashboard
const results = items;

// Sort by performance (response time)
const sortedBySpeed = [...results].sort((a, b) => 
  a.json.performance.response_time_ms - b.json.performance.response_time_ms
);

// Sort by quality (top similarity score)
const sortedByQuality = [...results].sort((a, b) => 
  b.json.quality.top_similarity - a.json.quality.top_similarity
);

// Create comparison dashboard
const dashboard = {
  query_tested: results[0]?.json.method_details.query || 'Unknown',
  comparison_timestamp: new Date().toISOString(),

  // Performance ranking
  speed_ranking: sortedBySpeed.map((item, index) => ({
    rank: index + 1,
    method: item.json.method_name,
    response_time_ms: item.json.performance.response_time_ms,
    medal: index === 0 ? 'ü•á' : index === 1 ? 'ü•à' : 'ü•â'
  })),

  // Quality ranking
  quality_ranking: sortedByQuality.map((item, index) => ({
    rank: index + 1,
    method: item.json.method_name, 
    top_similarity: item.json.quality.top_similarity,
    answer_length: item.json.quality.answer_length,
    medal: index === 0 ? 'ü•á' : index === 1 ? 'ü•à' : 'ü•â'
  })),

  // Method comparison table
  method_comparison: results.map(item => ({
    method: item.json.method_name,
    description: item.json.method_description,
    response_time: item.json.performance.response_time_ms + 'ms',
    contexts_found: item.json.performance.context_count,
    answer_quality: item.json.quality.top_similarity.toFixed(3),
    top_context_title: item.json.top_context?.title || 'None',
    success: item.json.performance.success ? '‚úÖ' : '‚ùå'
  })),

  // Winner analysis
  recommendations: {
    fastest_method: sortedBySpeed[0]?.json.method_name,
    best_quality: sortedByQuality[0]?.json.method_name,
    overall_winner: sortedByQuality[0]?.json.method_name, // Quality usually more important than speed
    reasoning: `For this query type, ${sortedByQuality[0]?.json.method_name} provided the best relevance while ${sortedBySpeed[0]?.json.method_name} was fastest.`
  },

  // Detailed results for deep dive
  detailed_results: results.map(item => item.json)
};

// Add visual summary
dashboard.visual_summary = `
üéØ COMPARISON COMPLETE
‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ
üèÜ Best Quality: ${dashboard.recommendations.best_quality}
‚ö° Fastest: ${dashboard.recommendations.fastest_method}
üéñÔ∏è  Overall Winner: ${dashboard.recommendations.overall_winner}

üí° ${dashboard.recommendations.reasoning}
`;

return [{ json: dashboard }];
