// Query type detection for demonstration
const items_data = items[0].json;
const query = items_data.active_query;

// Simple query classification logic
function classifyQuery(query) {
  const queryLower = query.toLowerCase();

  // Factual queries - benefit from sparse (exact terms)
  if (queryLower.includes('what is') || queryLower.includes('define') || queryLower.includes('meaning')) {
    return {
      type: 'factual',
      description: 'Definition/factual query â†’ sparse-heavy weights',
      expected_routing: 'Sparse-heavy (exact term matching)',
      predicted_alpha: 0.3
    };
  }

  // Conceptual queries - benefit from dense (semantic)
  if (queryLower.includes('how does') || queryLower.includes('explain') || queryLower.includes('why')) {
    return {
      type: 'conceptual',
      description: 'Conceptual query â†’ dense-heavy weights', 
      expected_routing: 'Dense-heavy (semantic understanding)',
      predicted_alpha: 0.7
    };
  }

  // Procedural queries - need exact steps (sparse)
  if (queryLower.includes('how to') || queryLower.includes('steps') || queryLower.includes('procedure')) {
    return {
      type: 'procedural',
      description: 'Procedural query â†’ sparse-heavy weights',
      expected_routing: 'Sparse-heavy (step-by-step matching)',
      predicted_alpha: 0.4
    };
  }

  // Default - exploratory queries
  return {
    type: 'exploratory',
    description: 'Exploratory query â†’ balanced weights',
    expected_routing: 'Balanced approach',
    predicted_alpha: 0.5
  };
}

const classification = classifyQuery(query);

const output = {
  ...items_data,
  query_analysis: {
    query: query,
    detected_type: classification.type,
    description: classification.description,
    expected_routing: classification.expected_routing,
    predicted_alpha: classification.predicted_alpha,
    reasoning: `The system detected "${query}" as a ${classification.type} query and will route accordingly.`
  }
};

return [{ json: output }];

// Read the API response from the previous node
const api = items[0].json;

// Pull query-analysis from the API metadata (or default to empty object)
const qa = api?.metadata?.query_analysis ?? {};

// Build a clean, uniform payload for the rest of the flow
const out = {
  query: api.query,
  method: 'adaptive',

  // what we think the query is + why
  predicted_analysis: {
    query_type: qa.detected_type ?? qa.type ?? 'unknown',
    predicted_alpha: qa.predicted_alpha ?? null,
    reasoning: qa.reasoning ?? ''
  },

  // keep the raw results for downstream formatting
  raw_response: {
    query: api.query,
    method: api.method ?? 'adaptive',
    source: api.source ?? 'wikipedia',
    results: api.results ?? []
  },

  // convenience fields
  num_results: api.num_results ?? (api.results?.length ?? 0),
  answer: api.answer ?? '',
  timing_ms: api.timing_ms ?? 0,
  top_contexts: api.top_contexts ?? [],
  metadata: api.metadata ?? {}
};

return [{ json: out }];

// Extract and format the API response
const response = items[0].json;

// Create formatted output
const output = {
  query: response.query,
  method: response.method_used,
  answer: response.answer,
  timing_ms: response.timing?.total_ms || 0,
  context_count: response.contexts?.length || 0,
  top_contexts: (response.contexts || []).slice(0, 3).map(ctx => ({
    title: ctx.title,
    similarity: ctx.sim_dense || ctx.similarity || 0,
    snippet: (ctx.content || '').substring(0, 200) + '...'
  })),
  success: response.success,
  raw_response: response
};

// Add success indicator
if (response.success) {
  output.status = 'âœ… SUCCESS';
} else {
  output.status = 'âŒ ERROR';
}

return [{ json: output }];

// Extract and format the hybrid search response
const response = items[0].json;

// Create detailed output for hybrid method
const output = {
  query: response.query,
  method: response.method_used,
  alpha_used: response.alpha || 0.5,
  answer: response.answer,
  timing_ms: response.timing?.total_ms || 0,
  context_count: response.contexts?.length || 0,

  // Enhanced context information for hybrid
  contexts_detailed: (response.contexts || []).slice(0, 3).map(ctx => ({
    title: ctx.title,
    dense_score: ctx.sd || ctx.sim_dense || 0,
    sparse_score: ctx.ss || ctx.sim_sparse || 0,
    hybrid_score: ctx.hybrid || 0,
    snippet: (ctx.content || '').substring(0, 150) + '...'
  })),

  // Performance metrics
  performance: {
    total_time: response.timing?.total_ms || 0,
    method: 'hybrid',
    alpha: response.alpha || 0.5,
    dense_weight: response.alpha || 0.5,
    sparse_weight: 1 - (response.alpha || 0.5)
  },

  success: response.success,
  raw_response: response
};

// Add visual indicators
if (response.success) {
  output.status = 'ðŸŽ¯ HYBRID SUCCESS';
  output.improvement_note = 'Check context scores - hybrid combines semantic + lexical!';
} else {
  output.status = 'âŒ ERROR';
}

return [{ json: output }];

