// Normalize your custom API output into a namespaced object { custom: {...} }
const r = items[0].json || {};
const out = {
  custom: {
    method: r.method_used || r.method || 'simple/dense',
    answer: r.answer || '',
    timing_ms: (r.timing && (r.timing.total_ms || r.timing_ms)) || 0,
    context_count: (r.contexts || []).length || (r.results || []).length || r.num_results || 0,
    top_contexts: (r.contexts || r.results || []).slice(0,3).map((c, i) => ({
      rank: i+1,
      title: c.title || c.metadata?.title || '',
      score: c.sim_dense || c.similarity || c.score || 0,
      snippet: (c.content || '').slice(0,220)
    })),
    raw: r
  }
};
return [{ json: out }];

// Normalize LC output, compute cost, and namespace as { lc: {...} }
const j = items[0].json || {};
const m = j.metrics || {};
// rough cost model for gpt-5-mini (input $0.25/M, output $2/M)
const cost = (m.tokens_input || 0) * 0.25e-6 + (m.tokens_output || 0) * 2.0e-6;
return [{
  json: {
    lc: {
      answer: j.answer || '',
      contexts: (j.contexts || []).slice(0,3).map((c,i)=>({
        rank: i+1,
        title: c.title || '',
        snippet: (c.content || '').slice(0,220)
      })),
      metrics: {
        llm_calls: m.llm_calls || 0,
        retriever_queries: m.retriever_queries || 0,
        db_round_trips: m.db_round_trips || 0,
        tokens_input: m.tokens_input || 0,
        tokens_output: m.tokens_output || 0,
        latency_ms: m.latency_ms || 0,
        approx_cost_usd: Number(cost.toFixed(6)),
        stages: m.stages || {}
      }
    }
  }
}];

// Add theoretical explosion math for LC path and keep namespaced under lc.expected
const x = items[0].json || {};
const lc = x.lc || {}; const st = lc.metrics || {};
// Pull the toggles that flowed from params through HTTP body (they're not in response),
// so we pass them along inside the request in real setups. For demo, approximate from observed data.
// We'll accept defaults matching the params node.
const cfg = {
  use_router: true,
  use_multiquery: true,
  multiquery_n: 4,
  use_compression: true,
  use_llm_rerank: true,
  answer_strategy: 'map_reduce',
  top_k: 5
};
const expected_retrievals = 1 + (cfg.use_multiquery ? cfg.multiquery_n : 0);
let expected_llm_calls = 0;
if (cfg.use_router) expected_llm_calls += 1;                 // router
if (cfg.use_multiquery) expected_llm_calls += cfg.multiquery_n; // paraphrases
if (cfg.use_compression) expected_llm_calls += cfg.top_k;       // per-doc compression
if (cfg.use_llm_rerank) expected_llm_calls += cfg.top_k;        // per-doc rerank
if (cfg.answer_strategy === 'map_reduce') expected_llm_calls += cfg.top_k + 1; // map+reduce
else if (cfg.answer_strategy === 'refine') expected_llm_calls += cfg.top_k;
else expected_llm_calls += 1; // stuff
x.lc.expected = {
  expected_retriever_queries: expected_retrievals,
  expected_llm_calls
};
return [{ json: x }];

