/**
 * Builds a side-by-side HTML report (binary file) including token & cost
 * for both Custom and LangChain branches, with fallbacks when metrics are missing.
 * Output: one item with binary { report } -> return via Form Ending (Return Binary File).
 */

// ---------- Tunable assumptions ----------
const TOKENS_PER_CHAR = 1 / 4;          // ~4 chars per token
const PROMPT_OVERHEAD_TOKENS = 120;     // per LLM call
const PRICING = {                       // $ per 1M tokens
  input_per_million: 0.25,
  output_per_million: 2.00,
};
// ---------------------------------------

const data   = (items[0] && items[0].json) || {};
const custom = data.custom || {};
const lc     = data.lc || {};
const m      = lc.metrics || {};
const e      = lc.expected || {};

const esc = (s) => String(s ?? "")
  .replace(/&/g, "&amp;")
  .replace(/</g, "&lt;")
  .replace(/>/g, "&gt;");

const n0 = (x) => Number(x || 0);
const nf = (x) => n0(x).toLocaleString("en-US");
const money = (usd) => `$${(Number(usd || 0)).toFixed(6)}`;

const tokensFromText = (t) => Math.max(0, Math.ceil(String(t || "").length * TOKENS_PER_CHAR));
const costUSD = (inTok, outTok) =>
  (inTok / 1e6) * PRICING.input_per_million + (outTok / 1e6) * PRICING.output_per_million;

// ---- Estimate tokens/cost for CUSTOM ----
const cCtxTokens = (custom.top_contexts || []).reduce((s, c) => s + tokensFromText(c.snippet || ""), 0);
const cAnsTokens = tokensFromText(custom.answer || "");
const cInTokens  = PROMPT_OVERHEAD_TOKENS + cCtxTokens;     // single generation
const cOutTokens = cAnsTokens;
const cCostUSD   = costUSD(cInTokens, cOutTokens);

// ---- Use LC metrics if available; otherwise estimate ----
const lcCalls     = m.llm_calls || e.expected_llm_calls || 1;
const lcCtxTokens = (lc.contexts || []).reduce((s, c) => s + tokensFromText(c.snippet || ""), 0);
const lcAnsTokens = tokensFromText(lc.answer || "");

// If LC service provided tokens/cost, take them; else fallback
const lcInTokens  = (m.tokens_input  && m.tokens_input  > 0) ? m.tokens_input  :
  (PROMPT_OVERHEAD_TOKENS * lcCalls) + (lcCtxTokens * Math.max(1, lcCalls - 1)); // map/reduce-ish reuse
const lcOutTokens = (m.tokens_output && m.tokens_output > 0) ? m.tokens_output : lcAnsTokens;
const lcCostUSD   = (m.approx_cost_usd && m.approx_cost_usd > 0) ? m.approx_cost_usd :
  costUSD(lcInTokens, lcOutTokens);

// ---------- UI helpers ----------
function barRow(label, c, l, fmt = (v)=>String(v)) {
  const max = Math.max(n0(c), n0(l), 1);
  const wC = n0(c) > 0 ? Math.max((n0(c)/max)*100, 2) : 0;
  const wL = n0(l) > 0 ? Math.max((n0(l)/max)*100, 2) : 0;
  return `
    <div class="row">
      <div class="label">${esc(label)}</div>
      <div class="bars">
        <div class="bar custom" style="width:${wC}%"><span>${fmt(c)}</span></div>
        <div class="bar lc"     style="width:${wL}%"><span>${fmt(l)}</span></div>
      </div>
      <div class="legend"><span class="dot custom"></span>Custom <span class="dot lc"></span>LangChain</div>
    </div>`;
}

function mkCards(list, withScore=false) {
  if (!Array.isArray(list) || list.length === 0) return `<div class="card">No contexts.</div>`;
  return list.slice(0,3).map((c,i)=>`
    <div class="card">
      <div class="rank">${c.rank ?? (i+1)}</div>
      ${withScore ? `<div class="score">${(n0(c.score)).toFixed(3)}</div>` : ""}
      <h4>${esc(c.title || "")}</h4>
      <p>${esc(String(c.snippet || "").replace(/\n+/g," "))}</p>
    </div>
  `).join("");
}

// ---------- Chart rows ----------
const chart = [
  barRow("LLM calls",            1,                 lcCalls,                     (v)=>nf(v)),
  barRow("Retriever queries",    1,                 m.retriever_queries || 0,    (v)=>nf(v)),
  barRow("DB round trips",       1,                 m.db_round_trips || 0,       (v)=>nf(v)),
  barRow("Latency (ms)",         custom.timing_ms||0, m.latency_ms || 0,         (v)=>nf(v)),
  barRow("Tokens in",            cInTokens,         lcInTokens,                  (v)=>nf(v)),
  barRow("Tokens out",           cOutTokens,        lcOutTokens,                 (v)=>nf(v)),
  barRow("Approx cost ($)",      cCostUSD,          lcCostUSD,                   (v)=>money(v)),
].join("");

// ---------- Cards ----------
const leftCards  = mkCards(custom.top_contexts || [], true);
const rightCards = mkCards(lc.contexts || [], false);

// ---------- HTML ----------
const html = `
<!doctype html><meta charset="utf-8">
<title>Custom vs LangChain â€” RAG Comparison</title>
<style>
:root{color-scheme:dark light}
