        <div>Contexts</div><div>${nf(custom.context_count)}</div>
        <div>Method</div><div>${esc(custom.method || "simple/dense")}</div>
        <div>Tokens in/out</div><div>${nf(cInTokens)} / ${nf(cOutTokens)}</div>
