  const wC = c > 0 ? Math.max( (c/maxVal)*100, 2) : 0;
  const wL = l > 0 ? Math.max( (l/maxVal)*100, 2) : 0;
  return `
    <div class="row">
      <div class="label">${esc(label)}</div>
      <div class="bars">
        <div class="bar custom" style="width:${wC}%"><span>${c ? esc(fmt(c)) : '0'}</span></div>
        <div class="bar lc"     style="width:${wL}%"><span>${l ? esc(fmt(l)) : '0'}</span></div>
      </div>
      <div class="legend">
        <span class="dot custom"></span>Custom
        <span class="dot lc"></span>LangChain
      </div>
    </div>`;
}

