const host = $json.headers?.host || 'localhost:5678';
const base = (host.startsWith('http') ? host : `http://${host}`);
const action = `${base}/webhook/rag-compare-run`;
const html = `<!doctype html><meta charset="utf-8"><title>RAG Compare</title>
<style>
  body{font:16px system-ui;margin:40px auto;max-width:720px}
  form{display:grid;gap:12px}
  input,button{padding:10px 12px;font:inherit}
  button{cursor:pointer}
  .hint{opacity:.7}
</style>
<h1>RAG: Custom vs LangChain</h1>
<p class="hint">This form posts to the POST webhook which runs both pipelines and responds with a styled HTML report + chart.</p>
<form method="post" action="${action}">
  <label>Query<br><input name="query" placeholder="What is WAL in PostgreSQL?" required></label>
  <button type="submit">Run comparison</button>
</form>`;
return [{ json: { html } }];

// Accept application/x-www-form-urlencoded OR JSON
const body = $json.body || {};
const query = body.query ?? $json.query?.q ?? '';
return [{ json: { query } }];

// Normalize your custom API output into { custom: {...} }
const r = items[0].json || {};
const out = {
  custom: {
    method: r.method_used || r.method || 'simple/dense',
    answer: r.answer || '',
    timing_ms: (r.timing && (r.timing.total_ms || r.timing_ms)) || 0,
    context_count: (r.contexts || []).length || (r.results || []).length || r.num_results || 0,
    top_contexts: (r.contexts || r.results || []).slice(0,3).map((c, i) => ({
      rank: i+1,
      title: c.title || c.metadata?.title || '',
      score: c.sim_dense || c.similarity || c.score || 0,
      snippet: (c.content || '').slice(0,220)
    })),
    raw: r
  }
};
return [{ json: out }];

// Normalize LC output, compute cost, and namespace as { lc: {...} }
const j = items[0].json || {};
const m = j.metrics || {};
const cost = (m.tokens_input || 0) * 0.25e-6 + (m.tokens_output || 0) * 2.0e-6; // gpt-5-mini
return [{
  json: {
    lc: {
      answer: j.answer || '',
      contexts: (j.contexts || []).slice(0,3).map((c,i)=>({
        rank: i+1,
        title: c.title || '',
        snippet: (c.content || '').slice(0,220)
      })),
      metrics: {
        llm_calls: m.llm_calls || 0,
        retriever_queries: m.retriever_queries || 0,
        db_round_trips: m.db_round_trips || 0,
        tokens_input: m.tokens_input || 0,
        tokens_output: m.tokens_output || 0,
        latency_ms: m.latency_ms || 0,
        approx_cost_usd: Number(cost.toFixed(6)),
        stages: m.stages || {}
      }
    }
  }
}];

// Add theoretical explosion math for LC and store under lc.expected
const x = items[0].json || {};
const lc = x.lc || {}; const st = lc.metrics || {};
const cfg = {
  use_router: true,
  use_multiquery: true,
  multiquery_n: 4,
  use_compression: true,
  use_llm_rerank: true,
  answer_strategy: 'map_reduce',
  top_k: 5
};
const expected_retrievals = 1 + (cfg.use_multiquery ? cfg.multiquery_n : 0);
let expected_llm_calls = 0;
if (cfg.use_router) expected_llm_calls += 1;
if (cfg.use_multiquery) expected_llm_calls += cfg.multiquery_n;
if (cfg.use_compression) expected_llm_calls += cfg.top_k;
if (cfg.use_llm_rerank) expected_llm_calls += cfg.top_k;
if (cfg.answer_strategy === 'map_reduce') expected_llm_calls += cfg.top_k + 1;
else if (cfg.answer_strategy === 'refine') expected_llm_calls += cfg.top_k;
else expected_llm_calls += 1;
if (!x.lc) x.lc = {};
x.lc.expected = { expected_retriever_queries: expected_retrievals, expected_llm_calls };
return [{ json: x }];

// Build HTML + compact bar chart
const data = items[0]?.json ?? {};
const custom = data.custom ?? {};
const lc     = data.lc     ?? {};
const m      = lc.metrics  ?? {};
const e      = lc.expected ?? {};
const esc = (s) => String(s ?? '').replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;');
const nf  = new Intl.NumberFormat('en-US');
const nfx = (x, d=0) => Number(x ?? 0).toLocaleString('en-US',{maximumFractionDigits:d,minimumFractionDigits:d});
const money = (x) => `$${Number(x ?? 0).toFixed(6)}`;
const leftCards = (custom.top_contexts ?? []).map(c => `
  <div class="card">
    <div class="rank">${c.rank ?? ''}</div>
    <div class="score">${Number(c.score ?? 0).toFixed(3)}</div>
    <h4>${esc(c.title ?? '')}</h4>
    <p>${esc((c.snippet ?? '').replace(/
+/g,' '))}</p>
  </div>`).join('') || '<div class="card">No contexts.</div>';
const rightCards = (lc.contexts ?? []).map((c,i) => `
  <div class="card">
    <div class="rank">${i+1}</div>
    <h4>${esc(c.title ?? '')}</h4>
    <p>${esc((c.snippet ?? '').replace(/
+/g,' '))}</p>
  </div>`).join('') || '<div class="card">No contexts.</div>';
const lcDown = !lc.answer && !m.latency_ms;
const metrics = [
  { label: 'LLM calls',          c: 1,                    l: m.llm_calls ?? 0,            fmt: v => nf.format(v) },
  { label: 'Retriever queries',   c: 1,                    l: m.retriever_queries ?? 0,     fmt: v => nf.format(v) },
  { label: 'DB round trips',      c: 1,                    l: m.db_round_trips ?? 0,        fmt: v => nf.format(v) },
  { label: 'Latency (ms)',        c: custom.timing_ms||0,  l: m.latency_ms ?? 0,            fmt: v => nf.format(v) },
  { label: 'Tokens in',           c: 0,                    l: m.tokens_input ?? 0,          fmt: v => nf.format(v) },
  { label: 'Tokens out',          c: 0,                    l: m.tokens_output ?? 0,         fmt: v => nf.format(v) },
  { label: 'Approx cost ($)',     c: 0,                    l: m.approx_cost_usd ?? 0,       fmt: v => money(v) }
];
function barRow({label,c,l,fmt}){
  const maxVal = Math.max(c,l,1);
  const wC = c>0 ? Math.max((c/maxVal)*100,2) : 0;
  const wL = l>0 ? Math.max((l/maxVal)*100,2) : 0;
  return `
<div class="row">
  <div class="label">${esc(label)}</div>
  <div class="bars">
    <div class="bar custom" style="width:${wC}%"><span>${c?esc(fmt(c)):'0'}</span></div>
    <div class="bar lc" style="width:${wL}%"><span>${l?esc(fmt(l)):'0'}</span></div>
  </div>
  <div class="legend">
    <span class="dot custom"></span>Custom
    <span class="dot lc"></span>LangChain
  </div>
</div>`;
}
const chartRows = metrics.map(barRow).join('');
const html = `
<style>
:root{color-scheme:dark light}
body{margin:0;background:#0b1020;color:#e7e8ea;font:16px/1.55 system-ui,-apple-system,Segoe UI,Inter,Roboto}
.wrap{max-width:1180px;margin:0 auto;padding:28px 18px 56px}
.grid2{display:grid;grid-template-columns:1fr 1fr;gap:18px}
.panel{background:#12182b;border:1px solid #27324b;border-radius:14px;padding:18px}
h1{margin:0 0 16px;font-size:22px}
h2{margin:10px 0 10px;font-size:18px;color:#cbd2ff}
.pill{display:inline-block;padding:4px 10px;border:1px solid #2a3146;border-radius:999px;background:#151a2a;font-size:12px;opacity:.9}
.answer{background:#11162a;border:1px solid #28314a;border-radius:12px;padding:12px;margin:10px 0 16px;white-space:pre-wrap}
.cards{display:grid;gap:10px}
.card{position:relative;background:#0f1527;border:1px solid #27324b;border-radius:12px;padding:12px}
.rank{position:absolute;left:-10px;top:-10px;background:#4b5bd7;border:2px solid #0b1020;border-radius:999px;width:28px;height:28px;display:grid;place-items:center;font-weight:700;color:white;font-size:13px}
.score{position:absolute;right:12px;top:12px;font-size:12px;opacity:.8}
.kv{display:grid;grid-template-columns:auto 1fr;gap:6px 12px;font-size:14px}
.kv div:nth-child(odd){opacity:.75}
.callouts{display:grid;gap:6px;font-size:14px}
.warn{color:#ffcf6d}
.good{color:#90ee90}
.banner{margin:0 0 14px;padding:10px;border-radius:10px;border:1px solid #5f4b00;background:#2a2200;color:#ffd87a}
.chart{background:#0e1426;border:1px solid #27324b;border-radius:14px;padding:16px;margin:0 0 18px}
.chart h2{margin-top:0}
.chart .row{display:grid;grid-template-columns:180px 1fr 160px;gap:10px;align-items:center;margin:12px 0}
.chart .label{opacity:.85}
.chart .bars{display:grid;grid-template-rows:16px 16px;gap:6px;background:#0b1020;border:1px solid #26304a;border-radius:10px;padding:10px}
.chart .bar{height:16px;border-radius:6px;position:relative;overflow:hidden}
.chart .bar span{position:absolute;right:8px;top:-1px;font-size:12px;opacity:.95}
.chart .bar.custom{background:#00d4a340;border:1px solid #00d4a3}
.chart .bar.lc{background:#6a7cff40;border:1px solid #6a7cff}
.chart .legend{font-size:12px;opacity:.9;display:flex;gap:10px;justify-content:flex-start}
.chart .dot{display:inline-block;width:10px;height:10px;border-radius:999px;margin-right:6px;vertical-align:middle}
.chart .dot.custom{background:#00d4a3}
.chart .dot.lc{background:#6a7cff}
</style>
<div class="wrap">
  <h1>Custom vs LangChain — RAG Comparison</h1>
  ${lcDown ? '<div class="banner">LangChain branch not available — showing Custom API only.</div>' : ''}
  <div class="chart">
    <h2>At a glance</h2>
    ${chartRows}
  </div>
  <div class="grid2">
    <div class="panel">
      <span class="pill">Custom API</span>
      <div class="answer">${esc(custom.answer ?? '')}</div>
      <div class="kv">
        <div>Latency</div><div>${custom.timing_ms ?? 0} ms</div>
        <div>Contexts</div><div>${custom.context_count ?? 0}</div>
        <div>Method</div><div>${esc(custom.method ?? 'simple/dense')}</div>
        <div>Calls</div><div>~1 HTTP (server handles internals)</div>
      </div>
      <h2>Top Passages</h2>
      <div class="cards">${leftCards}</div>
    </div>
    <div class="panel">
      <span class="pill">LangChain Path</span>
      <div class="answer">${esc(lc.answer ?? (lcDown ? '—' : ''))}</div>
      <div class="kv">
        <div>Latency</div><div>${nfx(m.latency_ms,0)} ms</div>
        <div>LLM calls (actual)</div><div>${nfx(m.llm_calls,0)}</div>
        <div>Retriever queries (actual)</div><div>${nfx(m.retriever_queries,0)}</div>
        <div>DB round trips (actual)</div><div>${nfx(m.db_round_trips,0)}</div>
        <div>Tokens in/out</div><div>${nfx(m.tokens_input,0)} / ${nfx(m.tokens_output,0)}</div>
        <div>Approx LLM cost</div><div>${money(m.approx_cost_usd)}</div>
        <div>Expected fan-out</div><div>${e.expected_retriever_queries ?? 0} queries • ${e.expected_llm_calls ?? 0} LLM calls</div>
      </div>
      <h2>Top Passages</h2>
      <div class="cards">${rightCards}</div>
      <h2>Why this can hurt</h2>
      <div class="callouts">
        <div class="warn">• MultiQuery fan-out → N× DB searches before answering</div>
        <div class="warn">• Per-doc compression & rerank → up to 2×k extra LLM calls</div>
        <div class="warn">• Map-Reduce answering → k+1 more LLM calls</div>
      </div>
    </div>
  </div>
</div>`;
return [{ json: { html, custom, lc } }];
