// Extract and format agentic search response
const response = $input.item.json;

// Determine decision indicator
const decision = response.metadata?.decision || 'unknown';
let decisionIcon = 'â“';
let decisionText = 'Unknown';

if (decision === 'search') {
  decisionIcon = 'ğŸ”„';
  decisionText = 'Agent used search tool';
} else if (decision === 'direct') {
  decisionIcon = 'âš¡';
  decisionText = 'Agent answered directly (no search needed)';
}

// Format sources if available
let sourcesText = '';
if (response.results && response.results.length > 0) {
  sourcesText = response.results.map((r, i) => {
    const title = r.metadata?.title || 'Unknown';
    return `[${i+1}] ${title}\n    ${r.content.substring(0, 150)}...\n    Score: ${r.score.toFixed(4)}`;
  }).join('\n\n');
} else {
  sourcesText = 'No sources (agent answered directly)';
}

// Format output
return {
  json: {
    query: response.query,
    method: response.method,
    decision: decision,
    decisionIcon: decisionIcon,
    decisionText: decisionText,
    toolUsed: response.metadata?.tool_used || false,
    searchCount: response.metadata?.search_count || 0,
    answer: response.answer,
    numResults: response.num_results,
    sources: sourcesText,
    cost: response.metadata?.cost || 0,
    costFormatted: `$${(response.metadata?.cost || 0).toFixed(4)}`
  }
};

